mapper.select:
SELECT 
	pro.pro_pk, 
	pro.pro_title, 
	pro.pro_title_color, 
	pro.pro_regdate, 
	pro.pro_thumbnail_image,

	CASE
		/** 알람 반복타입이 반복 없음 일 경우 */
		WHEN pro.pro_alarm_type = 0 THEN FALSE

		/** 매일 반복일 경우 */
		WHEN ( pro.pro_alarm_type = 1 AND 

				(  
					/** 매일 반복 시간과 현재 시간 비교, 알람 해제일과 현재 날짜 비교  */
					DATE_FORMAT(pro.pro_alarm_date, '%H:%i') <= DATE_FORMAT(NOW(), '%H:%i') AND 
					DATE_FORMAT(pt.pt_alarm_check_date, '%Y-%m-%d') <  DATE_FORMAT(NOW(), '%Y-%m-%d') 
				) OR
				( 
					/** 알람 해제일의 시간과 매일 반복 시간 비교  */
					(DATE_FORMAT(pt.pt_alarm_check_date, '%H:%i') < DATE_FORMAT(pro.pro_alarm_date, '%H:%i')) AND
					(DATE_FORMAT(NOW(), '%H:%i') >= DATE_FORMAT(pro.pro_alarm_date, '%H:%i')) AND
					(DATE_FORMAT(pt.pt_alarm_check_date, '%Y-%m-%d') =  DATE_FORMAT(NOW(), '%Y-%m-%d'))
				) 
				/** 알람 해제일이 없을 경우 */
			) OR pt.pt_alarm_check_date IS NULL THEN TRUE


		/** 요일 반복 일 경우 */
		WHEN ( pro.pro_alarm_type  = 2 AND (pro.pro_repeat_week = WEEKDAY(NOW()) AND 

					(
						DATE_FORMAT(pro.pro_alarm_date, '%H:%i') > DATE_FORMAT(pt.pt_alarm_check_date, '%H:%i') AND
						DATE_FORMAT(NOW(), '%H:%i') >= DATE_FORMAT(pro.pro_alarm_date, '%H:%i') AND
						DATE_FORMAT(NOW(), '%Y-%m-%d') > DATE_FORMAT(pt.pt_alarm_check_date, '%Y-%m-%d')
					)
				)

			 ) OR pt.pt_alarm_check_date IS NULL THEN TRUE

		/** 일자 반복 일 경우 */
		WHEN ( pro.pro_alarm_type = 3 AND (pro.pro_repeat_day = DAYOFMONTH(NOW()) AND DAYOFMONTH(NOW()) != DAYOFMONTH(pt.pt_alarm_check_date) AND

			DATE_FORMAT(pro.pro_alarm_date, '%H:%i') >= DATE_FORMAT(pt.pt_alarm_check_date, '%H:%i'))

			) OR pt.pt_alarm_check_date IS NULL THEN TRUE
		ELSE FALSE
	END AS pro_top_alarm_check
FROM 
	projects pro
LEFT JOIN project_target pt		ON pro.pro_pk = pt.pt_pro_fk
WHERE 
	pt.pt_admin_fk = #{adminPk}
	AND pt.pt_top_alarm_flag > 0
	AND pro.pro_delete_flag = 0
	AND pro.pro_finished_flag = 0

mapper.update:
UPDATE 
	projects
SET 
	pro_title_color = #{proTitleColor}
WHERE
 	pro_pk = #{proPk}

UPDATE
	projects
SET
	pro_alarm_date = #{proAlarmDateString}
WHERE
	pro_pk = #{proPk}

UPDATE
	projects
SET
	pro_alarm_type = #{proAlarmType}, pro_repeat_day = #{proRepeatDay}, pro_repeat_week = #{proRepeatWeek}
WHERE
	pro_pk = #{proPk}

mapper.select:
SELECT A.*, B.pt_pk, B.pt_admin_fk, B.pt_display_flag, B.pt_create_flag, B.pt_alarm_check_date, B.pt_top_alarm_flag, C.pd_pk, C.pd_detail, C.pd_display_flag, C.pd_file1, C.pd_file_real_name, C.pd_file_ext_type, C.pd_important, C.pd_finish_time, C.pd_admin, D.ptag_pk, D.ptag_admin_fk, D.ptag_title, E.pc_pk, E.pc_pd_fk, E.pc_detail, E.pc_regdate, F.admin_name, if(B.pt_admin_fk = #{adminPk}, true, false) AS pro_identify, IF(B.pt_admin_fk = F.admin_pk , true, false ) AS pro_inc FROM projects A LEFT OUTER JOIN project_target B ON A.pro_pk = B.pt_pro_fk LEFT OUTER JOIN admins F ON B.pt_admin_fk = F.admin_pk LEFT OUTER JOIN project_detail C ON A.pro_pk = C.pd_pro_fk /** 추가 사항 */ AND C.pd_display_flag = #{pdDisplayFlag}
AND C.pd_important = 1
LEFT OUTER JOIN project_tag D ON A.pro_pk = D.ptag_pro_fk LEFT OUTER JOIN project_comment E ON A.pro_pk = E.pc_pro_fk WHERE A.pro_pk = #{proPk} ORDER BY C.pd_finish_time DESC, E.pc_regdate DESC, D.ptag_pk

mapper.update:
UPDATE 
	project_target
SET 
	pt_top_alarm_flag = #{status}
WHERE 
	pt_pro_fk = #{ptProFk}
	AND  pt_admin_fk = #{adminPk}

UPDATE 
	project_detail
SET 
	pd_important = #{status}
WHERE
	pd_pk = #{pdPk}

mapper.insert:
INSERT INTO project_tag(
	ptag_pro_fk,
	ptag_admin_fk,
	ptag_title
)
VALUES(
	#{ptagProFk},
	#{ptagAdminFk},
	#{ptagTitle}
)

mapper.select:
SELECT ptag_title FROM project_tag A JOIN project_target B ON A.ptag_pro_fk = B.pt_pro_fk JOIN projects C ON B.pt_pro_fk = C.pro_pk WHERE C.pro_delete_flag = 0
AND B.pt_admin_fk = #{adminPk} AND A.ptag_admin_fk = #{adminPk}
group by ptag_title
