mapper.select:
SELECT or_serial_special_number, or_buyer_name, or_buyer_another_name, or_buyer_contract_number1, or_buyer_contract_number2, or_receiver_name, or_receiver_contract_number1, or_receiver_contract_number2, GROUP_CONCAT(DISTINCT or_delivery_message SEPARATOR ',') AS or_delivery_message, or_shipping_address, or_shipping_address_detail, or_shipping_address_number, or_sending_deadline, or_regdate FROM orders WHERE or_serial_special_number IN
#{specialNumber}
GROUP BY or_serial_special_number, or_buyer_name, or_buyer_another_name, or_buyer_contract_number1, or_buyer_contract_number2, or_receiver_name, or_receiver_contract_number1, or_receiver_contract_number2, or_shipping_address, or_shipping_address_detail, or_shipping_address_number, or_sending_deadline, or_regdate

mapper.update:
UPDATE
	orders
SET
	or_refunds = #{orRefunds}
WHERE
	or_pk = #{orPk}
