mapper.select:
SELECT SUM(INNE.or_total_price) AS total_price, COUNT(INNE.or_serial_special_number) AS total_order, MAX(or_total_price) AS max_price, MIN(or_total_price) AS min_price, MAX(or_amount) AS max_qty, MIN(or_amount) AS min_qty FROM( SELECT od.or_serial_special_number, SUM(od.or_amount) AS or_amount, SUM(od.or_total_price) AS or_total_price, CASE WHEN SUBSTRING(od.or_shipping_address, 1, INSTR(od.or_shipping_address, ' ')) = '강원' THEN '강원도' WHEN SUBSTRING(od.or_shipping_address, 1, INSTR(od.or_shipping_address, ' ')) = '경기' THEN '경기도' WHEN SUBSTRING(od.or_shipping_address, 1, INSTR(od.or_shipping_address, ' ')) = '경남' THEN '경상남도' WHEN SUBSTRING(od.or_shipping_address, 1, INSTR(od.or_shipping_address, ' ')) = '경북' THEN '경상북도' WHEN SUBSTRING(od.or_shipping_address, 1, INSTR(od.or_shipping_address, ' ')) = '광주' THEN '광주광역시' WHEN SUBSTRING(od.or_shipping_address, 1, INSTR(od.or_shipping_address, ' ')) = '대구' THEN '대구광역시' WHEN SUBSTRING(od.or_shipping_address, 1, INSTR(od.or_shipping_address, ' ')) = '대전' THEN '대전광역시' WHEN SUBSTRING(od.or_shipping_address, 1, INSTR(od.or_shipping_address, ' ')) = '부산' THEN '부산광역시' WHEN SUBSTRING(od.or_shipping_address, 1, INSTR(od.or_shipping_address, ' ')) = '서울' OR SUBSTRING(od.or_shipping_address, 1, INSTR(od.or_shipping_address, ' ')) = '서울시' THEN '서울특별시' WHEN SUBSTRING(od.or_shipping_address, 1, INSTR(od.or_shipping_address, ' ')) = '울산' THEN '울산광역시' WHEN SUBSTRING(od.or_shipping_address, 1, INSTR(od.or_shipping_address, ' ')) = '인천' THEN '인천광역시' WHEN SUBSTRING(od.or_shipping_address, 1, INSTR(od.or_shipping_address, ' ')) = '전남' THEN '전라남도' WHEN SUBSTRING(od.or_shipping_address, 1, INSTR(od.or_shipping_address, ' ')) = '전북' THEN '전라북도' WHEN SUBSTRING(od.or_shipping_address, 1, INSTR(od.or_shipping_address, ' ')) = '충남' THEN '충청남도' WHEN SUBSTRING(od.or_shipping_address, 1, INSTR(od.or_shipping_address, ' ')) = '충북' THEN '충청북도' ELSE SUBSTRING(od.or_shipping_address, 1, INSTR(od.or_shipping_address, ' ')) END AS CUT_ADDRESS FROM orders od WHERE
od.or_regdate >= #{dateStart} AND od.or_regdate < DATE_FORMAT(DATE_ADD(#{dateEnd}, INTERVAL 1 DAY), '%Y-%m-%d')
AND od.or_cancled_flag = 0 GROUP BY od.or_serial_special_number, CUT_ADDRESS ) INNE WHERE INNE.CUT_ADDRESS = #{searchKeyword}
