mapper.select:
SELECT pro.product_name, op.option_name, pql.pql_qty, pql.pql_admin_flag, pql.pql_regdate, pql.pql_reason FROM prod_qty_log pql INNER JOIN options op ON op.option_pk = pql.option_fk INNER JOIN products pro ON pro.product_pk = op.product_fk WHERE
pql.pql_regdate >= #{dateStart} AND pql.pql_regdate < DATE_FORMAT(DATE_ADD(#{dateEnd}, INTERVAL 1 DAY), '%Y-%m-%d')
ORDER BY pql.pql_regdate DESC LIMIT #{firstRecordIndex}, #{recordCountPerPage}

SELECT COUNT(*) FROM( SELECT pro.product_name, op.option_name, pql.pql_qty, pql.pql_admin_flag, pql.pql_regdate, pql.pql_reason FROM prod_qty_log pql INNER JOIN options op ON op.option_pk = pql.option_fk INNER JOIN products pro ON pro.product_pk = op.product_fk WHERE
pql.pql_regdate >= #{dateStart} AND pql.pql_regdate < DATE_FORMAT(DATE_ADD(#{dateEnd}, INTERVAL 1 DAY), '%Y-%m-%d')
) INNE
