mapper.select:
SELECT order_del_pk, order_del_order_number, order_del_product_order_number, order_del_product, order_del_product_option, order_del_product_qty, order_del_sending_deadline, INET_NTOA(order_del_ip) AS order_del_ip, order_del_admin, order_del_regdate FROM orders_delete WHERE
order_del_regdate >= #{dateStart} AND order_del_regdate < DATE_FORMAT(DATE_ADD(#{dateEnd}, INTERVAL 1 DAY), '%Y-%m-%d')
ORDER BY order_del_pk DESC LIMIT #{firstRecordIndex}, #{recordCountPerPage}
